# Java网络编程基础
## 实例代码
````Java
package network;

import java.io.BufferedReader;
import java.io.BufferedWriter;
import java.io.FileInputStream;
import java.io.FileOutputStream;
import java.io.IOException;
import java.io.InputStream;
import java.io.InputStreamReader;
import java.io.OutputStream;
import java.io.OutputStreamWriter;
import java.net.ServerSocket;
import java.net.Socket;
import java.time.LocalDateTime;

import org.junit.Test;

public class NetTest {
	@Test
	public void server3() {
		ServerSocket server = null;
		try {
			server = new ServerSocket(8888); // 绑定端口
			while (true) {
				System.out.println("服务器在8888端口监听中.....");
				final Socket socket1 = server.accept();
				;
				Runnable runner = new Runnable() {
					@Override
					public void run() {
						BufferedWriter bufferedWriter = null;
						try {
							bufferedWriter = new BufferedWriter(new OutputStreamWriter(socket1.getOutputStream()));
							bufferedWriter.write("我是服务器, 现在时间 : " + LocalDateTime.now());
							bufferedWriter.newLine();
							bufferedWriter.flush(); // 把数据真的刷入网线中
						} catch (Exception e) {
							e.printStackTrace();
						} finally {
							if (bufferedWriter != null) {
								try {
									bufferedWriter.close();
								} catch (Exception e2) {
								}
							}
							if (socket1 != null) {
								try {
									socket1.close();
								} catch (Exception e2) {
								}
							}
						}
						try {
							Thread.sleep(5000);
						} catch (InterruptedException e) {
							e.printStackTrace();
						}
					}
				};
				new Thread(runner).start();
			}
		} catch (Exception e) {
			e.printStackTrace();
		} finally {

			if (server != null) {
				try {
					System.out.println("服务器关闭....");
					server.close();
				} catch (Exception e2) {
				}
			}
		}
	}

	@Test
	public void client3() {
		Socket socket2 = null;
		BufferedReader bufferedReader = null;
		try {
			socket2 = new Socket("localhost", 8888);
			bufferedReader = new BufferedReader(new InputStreamReader(socket2.getInputStream()));
			String readLine = bufferedReader.readLine();
			System.out.println(readLine);
		} catch (Exception e) {
			e.printStackTrace();
		} finally {
			if (bufferedReader != null) {
				try {
					bufferedReader.close();
				} catch (Exception e2) {
				}
			}
			if (socket2 != null) {
				try {
					socket2.close();
				} catch (Exception e2) {
				}
			}
		}
	}

	// 从客户端发送文件给服务端，服务端保存到本地。并返回“发送成功”给客户端。并关闭相应的连接。
	@Test
	public void server2() {
		ServerSocket server = null;
		Socket socket1 = null;// 网络套接字

		InputStream nis = null;
		FileOutputStream fos = null;
		BufferedWriter netWriter = null;

		try {
			server = new ServerSocket(7777);// 指定服务器接口
			socket1 = server.accept();
			nis = socket1.getInputStream();
			fos = new FileOutputStream("朴树 - 猎户星座2.mp3");
			netWriter = new BufferedWriter(new OutputStreamWriter(socket1.getOutputStream()));

			byte[] buf = new byte[8192];
			int realCount = nis.read(buf);
			while (realCount != -1) {
				fos.write(buf, 0, realCount);
				realCount = nis.read(buf);
			}
			netWriter.write("发送成功");
			netWriter.newLine();
			netWriter.flush();

		} catch (Exception e) {
			e.printStackTrace();
		} finally {
			if (netWriter != null) {
				try {
					netWriter.close();
				} catch (Exception e2) {
				}
			}
			if (fos != null) {
				try {
					fos.close();
				} catch (Exception e2) {
				}
			}
			if (nis != null) {
				try {
					nis.close();
				} catch (Exception e2) {
				}
			}
			if (socket1 != null) {
				try {
					socket1.close();
				} catch (Exception e2) {
				}
			}
			if (server != null) {
				try {
					server.close();
				} catch (Exception e2) {
				}
			}
		}
	}

	@Test
	public void client2() {
		Socket socket2 = null;// 网络套接字

		FileInputStream fis = null;// 文件输入流
