    public static void maopaoSort(int[] arr){
        for (int i = 0; i <arr.length ; i++) {
            for(int j=1;j<arr.length-i;j++){//每一次循环，最大的数以及到了数组末尾，所以每一轮都可以少遍历一个数据
                if(arr[j]<arr[j-1]){
                    int tmp=arr[j];
                    arr[j]=arr[j-1];
                    arr[j-1]=tmp;
                }
            }
        }
    }
}
