				for (int i = 0; i < realCount; i++) {
					System.out.print(buf[i]);
					if (buf[i] == 10) {
						System.out.print(line++ + " ");
					}
				}
				// 2) 继续读后面的数据, 直到-1为止
				realCount = fileReader.read(buf);
			}
		} catch (Exception e) {
